"use strict";

var cliSize = require("cli-size");

/**
 * cliResize
 * Add a new resize event listener.
 *
 * @name CliResize
 * @function
 * @param {Function} fn The callback function which is called when the terminal is resized.
 */
module.exports = function (fn) {
  process.on("SIGWINCH", function () {
    return fn(cliSize());
  });
};